jQuery.fn.topLink=function(e){e=jQuery.extend({min:1,fadeSpeed:200},e);return this.each(function(){var n=$(this);n.hide();$(window).scroll(function(){if($(window).scrollTop()>=e.min){n.fadeIn(e.fadeSpeed)}else{n.fadeOut(e.fadeSpeed)}})})};$(document).ready(function(){$("#backtotopright").topLink({min:400,fadeSpeed:800});$("#backtotopright").click(function(e){e.preventDefault();$.scrollTo(0,300)})});